import subprocessimport sysimport threadingimport timeimport webbrowserdef run_streamlit():    subprocess.Popen(        [            sys.executable,            "-m",            "streamlit",            "run",            "app.py",            "--server.headless=true",            "--server.port=8501"        ],        stdout=subprocess.DEVNULL,        stderr=subprocess.DEVNULL    )def open_browser():    time.sleep(4)    webbrowser.open("http://localhost:8501")if __name__ == "__main__":    threading.Thread(target=run_streamlit, daemon=True).start()    open_browser()